/**
 *
 * [] to have a button element
 * [] to have al slot
 * [] to have a c-button class
 *
 * [] when a prop 'color' is passed have a certain classname
 * [] when a prop 'size' is passed have a certain classname
 *
 */

import { createLocalVue, shallowMount } from '@vue/test-utils'
import cButton from '../src/c-button.vue'

const localVue = createLocalVue()

describe('Button component', () => {
  let propsData
  let wrapper
  beforeEach(() => {
    propsData = {
      color: 'primary',
      size: 'medium'
    }

    wrapper = shallowMount(cButton, {
      localVue,
      propsData
    })
  })

  test('to have class "c-checkbox"', () => {
    expect(wrapper.classes()).toContain('c-button')
  })
})